<template>
    <div class="wrap">
        <ums-header title="CheckButton" :right="false" leftPartText="返回" @onLeftPartClick="back"></ums-header>
        <div class="part">
            <text class="title">自定义大小</text>
            <div class="row">
                <ums-check-button class="part1" size="56px"></ums-check-button>
                <ums-check-button class="part1" size="48px"></ums-check-button>
                <ums-check-button class="part1" size="32px"></ums-check-button>
            </div>
        </div>
        <div class="part">
            <text class="title">自定义颜色</text>
            <div class="row">
                <ums-check-button :checked="true"></ums-check-button>
                <ums-check-button color="red" :checked="true"></ums-check-button>
            </div>
        </div>
        <div class="part">
            <text class="title">所有状态</text>
            <div class="row">
                <ums-check-button :checked="true" :disable="true"></ums-check-button>
                <ums-check-button :checked="true" :disable="false"></ums-check-button>
                <ums-check-button :checked="false" :disable="true"></ums-check-button>
                <ums-check-button :checked="false" :disable="false"></ums-check-button>
            </div>
        </div>
        <div class="part">
            <text class="title">点击效果</text>
            <div class="row" @click="changeState">
                <ums-check-button :checked="state" :disable="false"></ums-check-button>
            </div>
        </div>
    </div>
</template>

<script>
    var navigator = weex.requireModule('navigator')
    import {UmsCheckButton, UmsHeader} from '../../'
    export default{
        data(){
            return {
                state:true,
            }
        },
        components:{
            UmsCheckButton,
            UmsHeader
        },
        methods:{
            changeState(){
                this.state = !this.state
            },
            back(){
                navigator.pop()
            }
        }
    }
</script>

<style lang='stylus' rel="stylesheet/stylus" scoped>
    .title
        font-size 48px
    .wrap
        align-items: center
    .row
        flex-direction: row
    .part1
        margin-right 10px
</style>