<template>
    <div class="wrapper" @click="showToast">
        <ums-header class="header" title="UmsMask" :right="false" leftPartText="返回" @onLeftPartClick="back"></ums-header>
        <ums-mask :show='show'
                    width='500px'
                    height='500px'
                  @overLayerClose='show = false'>
            <div style="width: 500px; height: 500px">
                <text style="text-align: center;font-size: 76px;color: #333">标题</text>
                <text style="color: #333">文字文字文字文字文字文字文字文字文字文字文字文字文字文字文字文字文字文字文字文字文字</text>
            </div>
        </ums-mask>
        <ums-button>Open</ums-button>
    </div>
</template>

<script>
    import {UmsButton,UmsMask,UmsHeader} from '../../index.js'
    var navigator = weex.requireModule('navigator')
    export default{
        data(){
            return {
                show: false
            }
        },
        components:{
            UmsButton,
            UmsHeader,
            UmsMask
        },
        methods:{
            showToast(){
                this.show = true
            },
            back(){
                navigator.pop()
            }
        }
    }
</script>

<style scoped>
    .header{
        position: fixed;
        top: 0px;
    }
    .wrapper{
        justify-content: center;
        align-items: center;
    }
</style>