<template>
  <div>
      <ums-header title="MessageBox" :right="false" leftPartText="返回" @onLeftPartClick="back"></ums-header>
      <div class="wrap">
          <div class="block" @click="alert"><text style="color: #ffffff">打开alert提示框</text></div>
          <div class="block" @click="confirm"><text style="color: #ffffff">打开confirm提示框</text></div>
          <div class="block" @click="promptInput"><text style="color: #ffffff">打来prompt input提示框</text></div>
          <div class="block" @click="promptRadio"><text style="color: #ffffff">打来prompt radio提示框</text></div>
          <div class="block" @click="loading"><text style="color: #ffffff">打开loading提示框</text></div>
          <div class="block" @click="print"><text style="color: #ffffff">打开print提示框</text></div>
          <div class="block" @click=""><text style="color: #ffffff">打开countdown提示框</text></div>
      </div>
  </div>
</template>

<script>
    import {UmsHeader ,UmsMessageBox} from '../../index.js'

    export default {
        components: {UmsHeader},
        mixins:[],
        data () {
            return {

            }
        },
        methods: {
            alert () {
                UmsMessageBox.alert('标题内容', '这是一个alert').then(() => {
                    UmsMessageBox.close()
                })
            },
            confirm () {
                UmsMessageBox.confirm('标题内容', '这是一个confirm', 10).then(() => {
                    UmsMessageBox.close()
                }).catch(() => {
                    UmsMessageBox.close()
                })
            },
            promptInput () {
                UmsMessageBox.promptInput('标题内容', '这是一个prompt input', 10).then(() => {
                    UmsMessageBox.close()
                }).catch(() => {
                    UmsMessageBox.close()
                })
            },
            promptRadio () {
                UmsMessageBox.promptRadio('标题内容', [
                    {
                        label: 'BCTC DEBIT(储蓄卡)',
                        value: '值A'
                    },
                    {
                        label: 'BCTC CREDIT(信用卡)',
                        value: '值B'
                    }]).then(() => {
                    UmsMessageBox.close()
                }).catch(() => {
                    UmsMessageBox.close()
                })
            },
            loading () {
                UmsMessageBox.loading('这是 loading',10)
                setTimeout(() => {
                    UmsMessageBox.close()
                }, 3000)
            },
            print () {
                UmsMessageBox.loading('这是print loading',10,{type: "print"});
                setTimeout(() => {
                    UmsMessageBox.close()
                }, 3000)
            }
        }
    }
</script>

<style lang="stylus" rel="styleSheet/stylus" >
