<template>
    <div class="body">
        <ums-progress class="test" :value="val">
            <text slot="start">0</text>
            <text slot="end" style="width: 80px;text-align: center">{{val==0 ?'':val + '%'}}</text>
        </ums-progress>
        <ums-button @click.native="change" size="small" style="margin-top:10px;">下载</ums-button>
        <ums-progress class="test" barWidth="500px">
            <text slot="start">0%</text>
            <text slot="end">100%</text>
        </ums-progress>
        <text>自定义高度</text>
        <ums-progress class="test" barHeight="20px" ></ums-progress>
        <text>自定义颜色</text>
        <ums-progress class="test" color="red"></ums-progress>
    </div>
</template>

<script>
    import {UmsButton,UmsProgress} from '../../index.js'
    export default{
        data(){
            return{
                val:0
            }
        },
        components:{
            UmsProgress,
            UmsButton
        },
        methods:{
            change(){
                var a = setInterval(()=>{
                    this.val < 100 ? this.val+=1 : clearInterval(a)
                },100)
            }
        }
    }
</script>

<style>
    .test{
        margin-top: 10px;
        margin-bottom: 20px;
    }
    .body{
        align-items: center;
        justify-content: center;
    }
</style>