<template>
    <div style="width:750px;">
        <ums-header title="UmsRange" leftPartText="返回" :right="false" @onLeftPartClick="back"></ums-header>
        <div class="content">
            <div class="title">
                <text class="inst">默认情况</text>
                <text class="val">value:{{value1}}</text>
            </div>
            <ums-range @rangeChange="listener1"></ums-range>
        </div>
        <div class="content">
            <div class="title">
                <text class="inst">禁用状态</text>
                <text class="val">value:{{value2}}</text>
            </div>
            <ums-range :disabled="true"></ums-range>
        </div>
        <div class="content">
            <div class="title">
                <text class="inst">初始值</text>
                <text class="val">value:{{value3}}</text>
            </div>
            <ums-range :initial="20" @rangeChange="listener2"></ums-range>
        </div>
        <div class="content">
            <div class="title">
                <text class="inst">自定义高度</text>
                <text class="val">value:{{value4}}</text>
            </div>
            <ums-range @rangeChange="listener3" barHeight="10px"></ums-range>
        </div>
        <div class="content">
            <div class="title">
                <text class="inst">轨道长度</text>
                <text class="val">value:{{value5}}</text>
            </div>
            <ums-range @rangeChange="listener4" length="500px"></ums-range>
        </div>
        <div class="content">
            <div class="title">
                <text class="inst">区间</text>
                <text class="val">value:{{value6}}</text>
            </div>
            <ums-range @rangeChange="listener5" :max="60" :min="20">
                <text slot="start">20</text>
                <text slot="end">60</text>
            </ums-range>
        </div>
        <div class="content">
            <div class="title">
                <text class="inst">不同的step</text>
                <text class="val">value:{{value7}}</text>
            </div>
            <ums-range @rangeChange="listener6" step="10">
                <text slot="start">0</text>
                <text slot="end">100</text>
            </ums-range>
        </div>
    </div>
</template>

<script>
    import {UmsHeader,UmsRange} from '../../index.js'
    export default{
        data(){
            return{
                value1: 0,
                value2: 0,
                value3: 20,
                value4: 0,
                value5: 0,
                value6: 20,
                value7: 0,
            }
        },
        components:{
            UmsRange,
            UmsHeader
        },
        methods:{
            back(){
                navigator.pop()
            },
            listener1(val){
                this.value1 = val
            },
            listener2(val){
                this.value3 = val
            },
            listener3(val){
                this.value4 = val
            },
            listener4(val){
                this.value5 = val
            },
            listener5(val){
                this.value6 = val
            },
            listener6(val){
                this.value7 = val
            }
        }
    }
</script>

<style rel="stylesheet/stylus" lang="stylus">
    .content
        flex-direction: row
        justify-content center
        margin-top 20px
    .inst
        font-size 32px
    .val
        font-size 24px
        color: #aaa
</style>
