<template>
    <div class = "wrapper">
        <ums-header title="UmsToast" :right="false" leftPartText="返回" @onLeftPartClick="back" class="header"></ums-header>
        <div class="btn" @click="photoToast">
            <text class="txt">带图片的toast</text>
        </div>
        <div class="btn" @click="photoSuccessToast">
            <text class="txt">带图片的toast</text>
        </div>
        <div class="btn" @click="fontToast">
            <text class="txt">只有文字的toast</text>
        </div>
        <div class="btn" @click="positionToast">
            <text class="txt">带不同位置的toast</text>
        </div>
    </div>
</template>

<script>
    import {UmsHeader, UmsToast} from '../../index.js';
    var navigator = weex.requireModule('navigator');
    export default{
        data(){

        },
        components:{
            UmsHeader
        },
        methods:{
            photoToast(){
                UmsToast({
                    message: "带图片的toast",
                    type:"waiting",
                    duration: 2000
                },()=>console.log('>>>>>>>>>>>>>>>> toast <<<<<<<<<<<<<<<<<<<<'))
            },
            photoSuccessToast() {
                UmsToast({
                    message: "带图片的toast",
                    type:"success",
                    duration: 2000
                },()=>console.log('>>>>>>>>>>>>>>>> toast <<<<<<<<<<<<<<<<<<<<'))
            },
            fontToast(){
                UmsToast({
                    message: "只有文字的toast",
                    duration: 2000
                })
            },
            positionToast(){
                UmsToast({
                    message: "不同位置的toast",
                    position: "bottom",
                    duration: 2000
                })
            },
            back(){
                navigator.pop()
            }
        }
    }
</script>

