<template>
    <div>
        <div class="overlayer"
             ref="overlayer"
             @click="overlayClickListener">
        </div>
    </div>
</template>

<script>
    const animation = weex.requireModule('animation')
    export default {
        data () {
            return {
            }
        },
        props: {
            overlayerOpacity: { // overlayer的透明度
                type: [Number, String],
                default: 0.6
            },
            show: {
                type: Boolean,
                default: false
            },
            hasAnimation: { // 是否有动画
                type: Boolean,
                default: true
            },
            duration: { // overlayer动画的周期
                type: Number,
                default: 100
            }
        },
        watch: {
            show (newVal){
                setTimeout(() => {
                    this.overLayAppear(newVal)
                }, 50)
            }
        },
        mounted () {
            this.overLayAppear(this.show)
        },
        methods: {
            overLayAppear (flag, duration = this.duration) {
                animation.transition(this.$refs.overlayer, {
                    styles: {
                        opacity: flag ? this.overlayerOpacity : 0
                    },
                    duration: this.hasAnimation ? duration : 0,
                    timingFunction: 'linear'
                },() => {
                    this.$emit('overlayerAppearFinish')
                })
            },
            overlayClickListener () {
                this.$emit('overlayerClick')
            }
        }
    }
</script>

<style lang='stylus' rel="stylesheet/stylus" scoped>
    .overlayer
        width 750px
        opacity 0
        position fixed
        top: 0
        left: 0
        bottom: 0
        right: 0
        background-color #000
</style>