<template>
    <div class="wrap">
        <div class="main" @click="inpFocus">
            <slot name='leftIcon'>
                <text class="icon" style="font-family:iconfont;font-size:36px;">&#xe60b;</text>
            </slot>
            <input ref="inp"
                   type="text"
                   class="search search-focus"
                   @blur="inpBlur"
                   @focus="inpFocus"
                   @input="onInput"
                   @change="onChange"
                   :autofocus="autofocus"
                   :placeholder="placeholder"/>
        </div>
        <div class="btn" @click="cancelBtn">
            <text class="txt">{{cancelText}}</text>
        </div>
    </div>
</template>

<script>
    var dom = weex.requireModule('dom');
    export default{
        data(){
            return {
                search:""
            }
        },
        props:{
            placeholder:{
                default:"搜索"
            },
            autofocus:{
                default:false,
                type:Boolean
            },
            'cancel-text':{
                default:'取消'
            },
        },
        methods:{
            inpFocus(){
                this.$refs.inp.focus && this.$refs.inp.focus();
                this.search = "";
            },
            inpBlur(){
                this.search = this.placeholder;
            },
            cancelBtn(){
                this.$refs.inp.blur && this.$refs.inp.blur();
            },
            onInput(event) {
                 this.search = event.value;
            },
            onChange(event) {
                this.$emit('change', event.value)
            }
        },
        beforeCreate(){
            dom.addRule('fontFace',{
                'fontFamily':'iconfont',
                'src':"url('../../../static/font/UmsIconFont.ttf')"
            })
        }
    }
</script>

